import './App.css';
import YoutubeVideo from './01/YoutubeVideo';
import YoutubeLylic from './01/YoutubeLylic';
import ResultLylic from './01/ResultLylic';
import LylicBlock from './01/LylicBlock';
import { Component } from 'react';

class App extends Component {
  constructor(props) {
    super(props);
    this.shuffle = this.shuffle.bind(this);
    this.selectCard = this.selectCard.bind(this);
    this.resetCard = this.resetCard.bind(this);
    this.validate = this.validate.bind(this);
    this.refresh = this.refresh.bind(this);
    let origin = ["여보세요", "거기", "누구", "없소", "나를", "사랑하는", "사람은"];
    const emptyarr = [];
    for(let i=0;i<origin.length;i++) {
      emptyarr.push("____");
    }
    let shufflearr = origin.slice();
    this.shuffle(shufflearr);
    const exampleItems = shufflearr.map((value, index) => <LylicBlock key={value} value={value} id={index} func={this.selectCard}/>);
    const selectItems = emptyarr.map((value, index) => <LylicBlock key={index} value={value} id={index}/>);
    console.log(origin);
    this.state = {
      origin: origin,
      arr: shufflearr,
      select: selectItems,
      link: "//www.youtube.com/embed//lJPALoI4Y8I?start=41&end=48",
      index: 0,
      example: exampleItems,
    }
    console.log(this.state.origin);
  }
  componentDidUpdate() {
    if(this.state.index === this.state.arr.length) {
      if(this.validate()) {
        console.log("Success!");
        console.log(this.state.select);
      }
      else {
        console.log("Fail!");
      }
    }
  }
  fail() {
    let arr = this.state.arr.slice();
    let change = this.state.select;
    change[0] = <LylicBlock className="red" key={this.state.index} value={arr[0]} id={0} func={this.resetCard}/>;
    this.setState((state, props) => ({
      select: change,
    }));
  }
  selectCard(e) {
    if(this.state.index > this.state.arr.length - 1)
      return;
    //console.log(e);
    //console.log(e.target.id);
    let arr = this.state.arr.slice();
    let change = this.state.select.slice();
    change[this.state.index] = <LylicBlock className="" key={this.state.index} value={arr[e.target.id]} id={e.target.id} func={this.resetCard}/>;

    //console.log(arr);
    this.setState((state, props) => ({
      index: this.state.index + 1,
      select: change,
    }));
  }
  resetCard(e) {
    const arr = this.state.arr;
    const emptyarr = [];
    for(let i=0;i<arr.length;i++) {
      emptyarr.push("____");
    }
    const selectItems = emptyarr.map((value, index) => <LylicBlock key={index} value={value} id={index}/>);
    //console.log(e);
    //console.log(e.target.id);
    this.setState((state, props) => ({
      index: 0,
      select: selectItems,
    }));
  }
  shuffle(a) {
    for (let i = a.length - 1; i > 0; i--) {
        const j = Math.floor(Math.random() * (i + 1));
        [a[i], a[j]] = [a[j], a[i]];
    }
    return a;
  }
  validate() {
    const selectedList = this.state.select.slice();
    const originalList = this.state.origin.slice();
    console.log(originalList);
    console.log(selectedList);
    for(let i=0;i<this.state.arr.length;i++) {
      if(originalList[i] !== selectedList[i].props.value) {
        console.log(i);
        console.log(selectedList[i].props.value);
        console.log(originalList[i]);
        return false;
      }
    }
    return true;
  }
  refresh() {
    let links = this.state.link.slice()+"s";
    this.setState((state, props) => ({
      link: links,
    }))
  }
  render() {
    return (
      <div className="App">
        <YoutubeVideo link={this.state.link}></YoutubeVideo>
        <ResultLylic items={this.state.select}/>
        <br />
        <br />
        <br />
        <YoutubeLylic func={this.selectCard} items={this.state.example}/>
        <button onClick={this.refresh()}></button>
      </div>
    );
  }
}

export default App;
