import './App.css';
import YoutubeVideo from './01/YoutubeVideo';
import YoutubeLylic from './01/YoutubeLylic';
import ResultLylic from './01/ResultLylic';
import LylicBlock from './01/LylicBlock';
import { Component } from 'react';

class App extends Component {
  constructor(props) {
    super(props);
    this.shuffle = this.shuffle.bind(this);
    this.selectCard = this.selectCard.bind(this);
    this.resetCard = this.resetCard.bind(this);
    this.validate = this.validate.bind(this);
    const arr = ["여보세요", "거기", "누구", "없소", "나를", "사랑하는"];
    const emptyarr = [];
    for(let i=0;i<arr.length;i++) {
      emptyarr.push("____");
    }
    const shufflearr = arr.slice();
    this.shuffle(shufflearr);
    const resultItems = shufflearr.map((value, index) => <LylicBlock key={value} value={value} id={index} func={this.selectCard}/>);
    const selectItems = emptyarr.map((value, index) => <LylicBlock key={index} value={value} id={index}/>);

    this.state = {
      result: arr,
      arr: shufflearr,
      select: selectItems,
      link: "//www.youtube.com/embed//lJPALoI4Y8I?start=41&end=48",
      index: 0,
      result: resultItems,
    }
  }
  selectCard(e) {
    if(this.state.index > this.state.arr.length - 1)
      return;
    //console.log(e);
    //console.log(e.target.id);
    let arr = this.state.arr.slice();
    let change = this.state.select.slice();
    change[this.state.index] = <LylicBlock key={this.state.index} value={arr[e.target.id]} id={e.target.id} func={this.resetCard}/>;

    //console.log(arr);
    this.setState((state, props) => ({
      index: this.state.index + 1,
      select: change,
    }));
    if(this.state.index === this.state.arr.length - 1) {
      if(this.validate())
        console.log("Success!");
      else
        console.log("Fail!");
    }
  }
  resetCard(e) {

    const arr = this.state.arr;
    const emptyarr = [];
    for(let i=0;i<arr.length;i++) {
      emptyarr.push("____");
    }
    const selectItems = emptyarr.map((value, index) => <LylicBlock key={index} value={value} id={index}/>);
    //console.log(e);
    //console.log(e.target.id);
    this.setState((state, props) => ({
      index: 0,
      select: selectItems,
    }));
  }
  shuffle(a) {
    for (let i = a.length - 1; i > 0; i--) {
        const j = Math.floor(Math.random() * (i + 1));
        [a[i], a[j]] = [a[j], a[i]];
    }
    return a;
  }
  validate() {
    const selectedList = this.state.select.slice();
    const originalList = this.state.arr.slice();
    //console.log(selectedList[0].props.value);
    for(let i=0;i<this.state.arr.length;i++) {
      if(result[i] !== selectedList[i].props.value) {
        return false;
      }
    }
    return true;
  }
  render() {
    return (
      <div className="App">
        <YoutubeVideo link={this.state.link}></YoutubeVideo>
        <ResultLylic items={this.state.select}/>
        <br />
        <br />
        <br />
        <YoutubeLylic func={this.selectCard} items={this.state.result}/>
      </div>
    );
  }
}

export default App;
