import './App.css';
import YoutubeVideo from './01/YoutubeVideo';
import YoutubeLylic from './01/YoutubeLylic';
import ResultLylic from './01/ResultLylic';

function App() {
  const arr = ["여보세요", "거기", "누구", "없소", "나를", "사랑하는"];
  const shufflearr = arr.slice();
  shuffle(shufflearr);
  return (
    <div className="App">
      <YoutubeVideo link="//www.youtube.com/embed//lJPALoI4Y8I?start=41&end=48"></YoutubeVideo>
      <ResultLylic size={shufflearr.length}/>
      <br />
      <YoutubeLylic array={shufflearr}/>
    </div>
  );
}

function shuffle(a) {
  for (let i = a.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [a[i], a[j]] = [a[j], a[i]];
  }
  return a;
}

export default App;
