import './App.css';
import YoutubeVideo from './01/YoutubeVideo';
import YoutubeLylic from './01/YoutubeLylic';
import ResultLylic from './01/ResultLylic';
import { Component } from 'react';

class App extends Component {
  constructor(props) {
    super(props);
    const arr = ["여보세요", "거기", "누구", "없소", "나를", "사랑하는"];
    const emptyarr = [];
    for(let i=0;i<arr.length;i++) {
      emptyarr.push("____");
    }
    const shufflearr = arr.slice();
    this.shuffle = this.shuffle.bind(this);
    this.selectCard = this.selectCard.bind(this);
    this.shuffle(shufflearr);
    this.state = {
      arr: shufflearr,
      select: emptyarr,
      link: "//www.youtube.com/embed//lJPALoI4Y8I?start=41&end=48",
      index: 0,
    }
  }
  selectCard(e) {
    console.log(e);
    console.log(e.target.id);
    let arr = this.state.arr.slice();
    let change = this.state.select.slice();

    change[this.state.index] = arr[e.target.id];
    this.setState({
      index: this.state.index + 1,
      select: change,
    });
  }
  shuffle(a) {
    for (let i = a.length - 1; i > 0; i--) {
        const j = Math.floor(Math.random() * (i + 1));
        [a[i], a[j]] = [a[j], a[i]];
    }
    return a;
  }
  render() {
    return (
      <div className="App">
        <YoutubeVideo link={this.state.link}></YoutubeVideo>
        <ResultLylic array={this.state.select}/>
        <br />
        <br />
        <br />
        <YoutubeLylic array={this.state.arr} func={this.selectCard}/>
      </div>
    );
  }
}

export default App;
